sys_script_include.script:
var CheckMyHistory = Class.create();
CheckMyHistory.prototype = {
    initialize: function() {
    },

	validate: function() {
		var myUser = new GlideRecord('sys_user');
		myUser.query();
		while(myUser.next()){

		var roles = new GlideRecord('sys_user_has_role');
		roles.query();
			while(roles.next()){

				if(roles.user == myUser.sys_id){

		var grabACLRoles = new GlideRecord('sys_security_acl');
		grabACLRoles.addQuery('name', 'STARTSWITH', 'x_pir_employee_car');
		grabACLRoles.query();
			gs.info("getting count " + grabACLRoles.getRowCount());
		while(grabACLRoles.next()){
			var grabRole = new GlideRecord('sys_security_acl_role');
			grabRole.query();
			grabRole.next();
			gs.info("all roles " + grabRole.sys_user_role);
			if(myUser.roles.indexOf(grabRole.sys_user_role) > 1){
				return true;
			}
			else
				return false;
		}
					}
				}
		}
	},

    type: 'CheckMyHistory'
};
